// No direct access
defined('_JEXEC') or die;

jimport('joomla.application.component.helper');

/**
 * Apartment model for the rental component.
 *
 * @package		Joomla.Site
 * @subpackage	com_rental
 * @since		1.6
 */
class RentalModelAjax_Location extends JModel
{
	/**
	 * Gets a list of apartment
	 *
	 * @return	Ajax Location object or false.
	 * @since	1.6
	 */
	function getItems()
	{
		//init vars
		$db			= $this->getDbo();
		$query		= $db->getQuery(true);
		
		$result = new stdClass();
		
		$borough_id = JRequest::getVar('borough_id');
		
		if(!$borough_id)
			return false;
		
		//get sub category
		$query->select('*')
				->from('#__categories')
				->where('extension = "com_rental"')
				->where('parent_id = ' . (int) $borough_id)
				->order('title')
			;
			
		$db->setQuery($query);
		$cats = $db->loadObjectList();
		
		$result->sub_category = $cats;
		
		$catid = array();
		
		$catid[] = $borough_id;
		
		//get cat id
		foreach ($cats as $cat)
			$catid[] = $cat->id;
		
		$listCatId = implode(',', $catid);
		
		$query		= $db->getQuery(true);
		
		//get location
		$query->select('*')
				->from('#__retal_location')
				->where('catid IN (' . $listCatId . ')')
			;
			
		$db->setQuery($query);
		$locations = $db->loadObjectList();
		
		$result->locations = $locations;
		
// 		var_dump($result);
			
		return $result;
	}
	
	public function getBoroughInfo()
	{
		$borough_id = JRequest::getVar('borough_id');
		
		$db			= $this->getDbo();
		$query		= $db->getQuery(true);
		
		$query->select('*')->from('#__categories')->where('id = ' . (int) $borough_id);
		
		$db->setQuery($query);
		$borough = $db->loadObject();
			
		return $borough;
	}
}